import React, {useState} from 'react';

import StationItem from './stationItem';

const StationList = (props) => {

    return (
        <div>
            <select name="stations" id="stationPicker-{props.pickerId}" onChange={updateCurrentStationHandler}>
            {
                props.stations.map(station => {
                    return <StationItem station={station} pickerId={props.pickerId} />
                })
            }
            </select>
        </div>
    )
}

export default StationList;
