import React from 'react';

const StationItem = (props) => {

    const selectCityHandler = (event) => {

        const selectedOption = event.target.value;
        console.log("selected Option Station on : " , props.picker.id, " is : ", selectedOption);

        props.updateCurrentStations(currStations => {
            if(props.pickerId == 1) return {
                city1: currStations.city1,
                city2: selectedOption 
            }
            else return {
                city1: selectedOption,
                city2: currStations.city2
            }
        })
    }

    return (
        <option key={props.station.id} onClick={selectCityHandler} value={props.station.id}>{props.station.name}</option>
    )
}

export default StationItem;
