        const currCity = event.target.value;
        toggleActivate(true);

        updateCurrentCity(async old => {
            console.log("marker 1");
            await fetch(`http://localhost:4100/ida?city=${currCity}`).then(
            res => {
                    // console.log("here second");
                    return res.json();
