                    console.log("marker 2");
                    updateAllCityStation(async old => {
                        console.log("marker 3 ");

                        function getUsers() {
                            return new Promise((resolve, reject) => {
                              resolve({
                                city1: data.toFill[0],
                                city2: data.toFill[0]
                            })
                            });
                          }

                        await getUsers().then(updateCurrentStations);
                        console.log("marker 6");

                        return data.toFill
                    });
                    console.log("marker 7");
                    console.log(allCityStations);
                    console.log(data.toFill);

                }
            )
            return currCity;
        })

        // console.log("here first");

