import React, {useState} from 'react';

import CityItem from './CityItem';
import StationList from '../Station/stationList';

const CityList = (props) => {

    // props.cities is an array of city names and ids
    let [activate, toggleActivate] = useState(false);

    // const allCityStations = {
    //     Vice: [
    //         {id: 121, name: "A"},
    //         {id: 122, name: "B"},
    //         {id: 123, name: "C"}
    //     ],
    //     Delhi: [
    //         {id: 221, name: "AA"},
    //         {id: 222, name: "BB"}
    //     ]
    // };

    let [allCityStations, updateAllCityStation] = useState([]);

    let [currentCity, updateCurrentCity] = useState("Vice");

    let [currentStations, updateCurrentStations] = useState({
        city1: "dummy",
        city2: "dummy"
    });

    const getData = (data) => {
        updateAllCityStation(data.toFill);
        console.log(allCityStations);
    }

    const updateCurrentCityHandler = async (event) => {

        //? GET DATA OF STATION LIST HERE

        //! trigger disables
        //* async code awaited in self functions
        //* here we only have to pass the city name and we have that already in event.target.value

        const currCity = event.target.value;
        toggleActivate(true);

        console.log("here first");
        await fetch(`http://localhost:4100/ida?city=${currCity}`).then(
            res => {
                console.log("here second");
                return res.json();
            }
        ).then(
            data => {
                console.log("here");
                getData(data);
            }
        )

        console.log("marker 1");

        updateCurrentCity(old => {
            console.log("marker 2");
            updateCurrentStations(old => ({
                city1: allCityStations[0],
                city2: allCityStations[0]
            }));
            // updateAllCityStation(old => allCityStations)
            return currCity;
        });

        console.log("marker 3");
        console.log(allCityStations);
        toggleActivate(false);

        //* async code to fill both station lists

        //! de-trigger disables

        // console.log(curr);
    }

    async function stall(stallTime = 5000) {
        await new Promise(resolve => {

            return setTimeout(resolve, stallTime);
        });
        console.log('It will be printed 2-nd with delay');
      }

    const calculateBtnHandler = async () => {
        console.log("start");

        console.log("city", currentCity);
        console.dir(currentStations);
        //! enable same disables

        toggleActivate(true);

        //* mimcking the await of call to backend

        await stall();

        toggleActivate(false);

        console.log('end');
    }

    // console.log(currentCity);
    // console.log("here");
    // console.dir(currentStations);

    return (
        <div>
            <h1>Here</h1>
            <select name="cities" id="cityPicker" onChange={updateCurrentCityHandler} disabled={activate}>
            {
                props.cities.map(city => {
                    return <CityItem city={city} />
                })
            }
            </select>

            <StationList stations={allCityStations} pickerId={1} updateCurrentStations={updateCurrentStations} disabled={activate} />
            <StationList stations={allCityStations} pickerId={2} updateCurrentStations={updateCurrentStations} disabled={activate} />


            <button type="submit" onClick={calculateBtnHandler} disabled={activate}>Calculate</button>
        </div>
    )
}

export default CityList;
