import React, {useState} from 'react';

import CityItem from './CityItem';
import StationList from '../Station/stationList';


const CityList = (props) => {

    let [activate, toggleActivate] = useState(false);
    let [currentCity, updateCurrentCity] = useState("newVice2");
    let allStations = [];

    const updateCurrentCityHandler = async (event) => {

        const currCity = event.target.value;
        toggleActivate(true);

        await fetch(`http://localhost:4100/ida?city=${currCity}`)
        .then(
            res => {
                return res.json();
            })
        .then(
            data => {
                console.log("marker 2");
                allStations = data.toFill;
            })

        updateCurrentCity(old => currCity);

        // console.log(allStations);

        console.log("marker ");
        // console.log(allStations);

        toggleActivate(false);
    }

    return (
        <div>
            <h1>Here</h1>
            <select name="cities" id="cityPicker" onChange={updateCurrentCityHandler} disabled={activate}>
            {
                props.cities.map(city => {
                    return <CityItem city={city} />
                })
            }
            </select>

            <StationList city={currentCity} stations={allStations} disabled={activate} />
            {/* <StationList city={currentCity} stations={allStations} pickerId={2} disabled={activate} /> */}

        </div>
    )
}

export default CityList;
