import React, {useState} from 'react';

import StationItem from './stationItem';

const StationList = (props) => {

    // const updateCurrentStationHandler = (event) => {
    //     const curr = event.target.value;
    //     props.updateCurrentStations(currStations => {
    //         if(props.pickerId == 2) return {
    //             city1: currStations.city1,
    //             city2: curr 
    //         }
    //         else return {
    //             city1: curr,
    //             city2: currStations.city2
    //         }
    //     })
    // }
    let station1 = props.staions[0], station2 = props.stations[0];

    const stationChangeHandler = (event) => {
        if(event.target.id == "stationPicker-1") {
            station1 = event.target.value;
        }
        else if(event.target.id == "stationPicker-2") {
            station2 = event.target.value;
        }
    }

    return (
        <div>
            <select name="stations" id="stationPicker-1" onChange={stationChangeHandler} disabled={props.disabled}>
            {
                props.stations.map(station => {
                    return <StationItem station={station} />
                })
            }
            </select>

            <select name="stations" id="stationPicker-2" onChange={stationChangeHandler} disabled={props.disabled}>
            {
                props.stations.map(station => {
                    return <StationItem station={station} />
                })
            }
            </select>
        </div>
    )
}

export default StationList;
