import React from 'react';

const CityItem = (props) => {

    const selectCityHandle = (event) => {

        const selectedOption = event.target.value;
        console.log("selected Option City : ", selectedOption);

        props.updateCurrentCity(selectedOption);
    }

    return (
        <option key={props.city.id} onClick={selectCityHandle} value={props.city.name}>{props.city.name}</option>
    )
}

export default CityItem;
