import React, {useState} from 'react';

import CityItem from './CityItem';
import StationList from '../Station/stationList';

const CityList = (props) => {

    // props.cities is an array of city names and ids

    const allCityStations = {
        Vice: [
            {id: 121, name: "A"},
            {id: 122, name: "B"},
            {id: 123, name: "C"}
        ],
        Delhi: [
            {id: 221, name: "AA"},
            {id: 222, name: "BB"}
        ]
    };

    let [currentStations, updateCurrentStations] = useState({
        city1: props.stations[0].id,
        city2: props.station[0].id
    });

    let [currentCity, updateCurrentCity] = useState("Vice");

    return (
        <div>
            <select name="cities" id="cityPicker">
            {
                props.cities.map(city => {
                    return <CityItem city={city} updateCurrentCity={updateCurrentCity} />
                })
            }
            </select>

            <StationList stations={allCityStations[currentCity]} pickerId={1} updateCurrentStations={updateCurrentStations} />
            <StationList stations={allCityStations[currentCity]} pickerId={2} updateCurrentStations={updateCurrentStations} />

        </div>
    )
}

export default CityList;
